var h = require('mercury').h;

var image = function image(state) {
  return h('html', [
    h('head', [
      h('title', 'Hello Mt. Wellington!'),
      h('link', {
        'href': '/css/style.css',
        'rel': 'stylesheet',
        'type': 'text/css'
      })
    ]),
    h('body', {
      'style': {
        'background-image': 'url(' + state.dataURI + ')'
      }
    }, [
      h('img', {
        'class': 'cover-image',
        'src': state.imageURI,
        'alt': 'Mt. Wellington as seen at ' + state.timeStamp
      })
    ])
  ]);
};

module.exports = image;
